<?php    //default language    $language = 'en';        //detect root    $LANGUAGE_ROOT = "";    if (is_dir("../language"))        $LANGUAGE_ROOT = $LANGUAGE_ROOT."../";    else        if (is_dir("../language"))            $LANGUAGE_ROOT = $LANGUAGE_ROOT."../";        //if we have something filled in language    if (!empty($_GET["l"]))    {        $temp_l = strtolower($_GET["l"]);        //and the language file really exists on the disk        if (file_exists($LANGUAGE_ROOT."language/".$temp_l.".php"))        {            $language = $temp_l;                    }    }    else    {        //try using POST        /*        if (!empty($_POST["l"]))        {            $temp_l = strtolower($_POST["l"]);            //and the language file really exists on the disk            if (file_exists($LANGUAGE_ROOT."language/".$temp_l.".php"))            {                $language = $temp_l;                        }        }        */    }?>